import styled from "styled-components";
import React, { useRef, useEffect } from "react";
import { toast } from "react-toastify";
import axios from "axios";


const FormContainer = styled.form`
  display: flex;
  align-items: flex-end;
  gap: 20px;
  flex-wrap: wrap;
  background-color: #fff;
  padding: 20px;
  box-shadow: 0px 0px 5px #ccc;
  border-radius: 10px;
`;

const InputArea = styled.div`
  display: flex;
  flex-direction: column;
`;

const Input = styled.input`
  width: 120px;
  padding: 0 10px;
  border: 1px solid #bbb;
  border-radius: 10px;
  height: 40px;
`;

const Button = styled.button`
  padding: 10px;
  cursor: pointer;
  border-radius: 5px;
  border: none;
  background-color: #2c73d2;
  color: white;
  height: 42px;
`;


const Label = styled.label``;


const Form = ({ getProducts, onEdit, setOnEdit}) => {
    const ref = useRef();

    useEffect(() => {
      if (onEdit) {
        const product = ref.current;
  
        user.codigo.value = onEdit.codigo;
        user.nome.value = onEdit.nome;
        user.descricao.value = onEdit.descricao;
        user.preco.value = onEdit.preco;
      }
    }, [onEdit]);

    const handleSubmit = async (e) => {
      e.preventDefault();

      const user = ref.current;

      if (
        !user.codigo.value ||
        !user.nome.value ||
        !user.descricao.value ||
        !user.preco.value
      ) {
        return toast.warn("Preencha todos os campos!");
      }

      if (onEdit) {
        await axios
          .put("http://localhost:8800/" + onEdit.id, {
            nome: user.codigo.value,
            nome: user.nome.value,
            descricao: user.descricao.value,
            preco: user.preco.value,
          })
          .then(({ data }) => toast.success(data))
          .catch(({ data }) => toast.error(data));
      } else {
        await axios
          .post("http://localhost:8800", {
            nome: user.codigo.value,  
            nome: user.nome.value,
            descricao: user.descricao.value,
            preco: user.preco.value,
          })
          .then(({ data }) => toast.success(data))
          .catch(({ data }) => toast.error(data));
      }
      user.codigo.value = "";
      user.nome.value = "";
      user.descricao.value = "";
      user.preco.value = "";

      setOnEdit(null);
      getProducts();

    };
   
    return (
        <FormContainer ref={ref} onSubmit={handleSubmit}>
            <InputArea>
                <Label>Código</Label>
                <Input name="codigo"/>
            </InputArea>
            <InputArea>
                <Label>Nome</Label>
                <Input name="nome"/>
            </InputArea>
            <InputArea>
                <Label>Descrição</Label>
                <Input name="descricao"/>
            </InputArea>
            <InputArea>
                <Label>Preço</Label>
                <Input name="preco" type="number"/>
            </InputArea>
            
            <Button type="submit">SALVAR</Button>
        </FormContainer>
    );
};

export default Form;
